            ]
          }
        },
        {
          "y": 1.92,
          "color": colors[4],
          "drilldown": {
            "name": "Opera",
            "categories": [
              "Opera v50.0",
              "Opera v49.0",
              "Opera v12.1"
            ],
            "data": [
              0.96,
              0.82,
              0.14
            ]
          }
        },
        {
          "y": 7.62,
          "color": colors[6],
          "drilldown": {
            "name": 'Other',
            "categories": [
              'Other'
            ],
            "data": [
              7.62
            ]
          }
        }
      ],
      browserData = [],
      versionsData = [],
      i,
      j,
      dataLen = data.length,
      drillDataLen,
      brightness;
    for (i = 0; i < dataLen; i += 1) {
      browserData.push({
        name: categories[i],
        y: data[i].y,
        color: data[i].color
      });
      drillDataLen = data[i].drilldown.data.length;
      for (j = 0; j < drillDataLen; j += 1) {
        brightness = 0.2 - (j / drillDataLen) / 5;
        versionsData.push({
          name: data[i].drilldown.categories[j],
          y: data[i].drilldown.data[j],
          color: Highcharts.Color(data[i].color).brighten(brightness).get()
        });
      }
    }
