import React, {Component} from 'react';
import Highcharts from 'highcharts';
import Treemap from 'highcharts/modules/treemap';
import Heatmap from 'highcharts/modules/heatmap';
import $ from 'jquery';

export default class LargeTreeMap extends Component {
  componentDidMount() {
    $.getJSON(
      'https://cdn.rawgit.com/highcharts/highcharts/057b672172ccc6c08fe7dbb27fc17ebca3f5b770/samples/data/world-mortality.json',
      function (data) {
        let points = [],
          regionP,
          regionVal,
          regionI = 0,
          countryP,
          countryI,
          causeP,
          causeI,
          region,
          country,
          cause,
          causeName = {
            'Communicable & other Group I': 'Communicable diseases',
            'Noncommunicable diseases': 'Non-communicable diseases',
            'Injuries': 'Injuries'
          };
        for (region in data) {
          if (data.hasOwnProperty(region)) {
            regionVal = 0;
            regionP = {
              id: 'id_' + regionI,
              name: region,
              color: Highcharts.getOptions().colors[regionI]
            };
            countryI = 0;
            for (country in data[region]) {
              if (data[region].hasOwnProperty(country)) {
                countryP = {
                  id: regionP.id + '_' + countryI,
                  name: country,
                  parent: regionP.id
                };
                points.push(countryP);
                causeI = 0;
                for (cause in data[region][country]) {
                  if (data[region][country].hasOwnProperty(cause)) {
                    causeP = {
                      id: countryP.id + '_' + causeI,
                      name: causeName[cause],
                      parent: countryP.id,
                      value: Math.round(+data[region][country][cause])
                    };
                    regionVal += causeP.value;
                    points.push(causeP);
                    causeI = causeI + 1;
                  }
                }
                countryI = countryI + 1;
              }
            }
            regionP.value = Math.round(regionVal / countryI);
            points.push(regionP);
            regionI = regionI + 1;
          }
        }
        Treemap(Highcharts);
        Heatmap(Highcharts);
        Highcharts.chart('large-tree-container', {
          series: [
            {
              type: 'treemap',
              layoutAlgorithm: 'squarified',
              allowDrillToNode: true,
              animationLimit: 1000,
              dataLabels: {
                enabled: false
              },
              levelIsConstant: false,
              levels: [
                {
                  level: 1,
                  dataLabels: {
                    enabled: true
                  },
                  borderWidth: 3
                }
              ],
              data: points
            }
          ],
          subtitle: {
            text: 'Click points to drill down. Source: <a href="http://apps.who.int/gho/data/node.main.12?lang=en">WHO</a>.'
          },
          title: {
            text: 'Global Mortality Rate 2012, per 100 000 population'
          }
        });
      }
    );
  }

  render() {
    return (
      <div id='large-tree-container'/>
    );
  }
}

import React, {Component} from 'react';
import Highcharts from 'highcharts';
import Treemap from 'highcharts/modules/treemap';

export default class LevelsTreeMap extends Component {
  componentDidMount() {
    Treemap(Highcharts);
    Highcharts.chart('tree-container', {
      series: [{
        type: "treemap",
        layoutAlgorithm: 'stripes',
        alternateStartingDirection: true,
