import React, {Component} from "react";
import PropTypes from "prop-types";
import {getClasses} from "../../Helpers/index";

class Button extends Component {

  _buildClasses () {
    let classes = ['btn'];
    let size    = `btn-${this.props.size}`;
    classes.push(size);


    if(this.props.disabled) {
      classes.push('disabled');
    }
    if(this.props.outlined){
      classes.push(`btn-outline-${this.props.type}`);
    }else{
      classes.push(`btn-${this.props.type}`);
    }
    if(this.props.active){
      classes.push('active');
    }
    if(this.props.block){
      classes.push('btn-block');
    }
    return classes.join(" ");
  }

  render() {
    return (
        <button type="button" 
                className={this._buildClasses()}
                data-toggle={this.props.data}
                aria-pressed={this.props.pressed} 
                autoComplete={this.props.autocomplete}>
          {this.props.ButtonName || this.props.children}
        </button>
    );
  }
}

Button.defaultProps = {
  type : 'default' ,
  size : 'sm',
};

Button.propTypes = {
    type : PropTypes.string,
    ButtonName : PropTypes.string,
    size : PropTypes.string,
};


export default Button;

