	_getChildrenWithProps() {
		const props = this._getChildrenProps();
		return React.Children.map(this.props.children, child => this._cloneChild(child, props), null);
	}

	render() {
		return (
			<li className={getClasses('list-group-item', this.props.classes)}>
				{this._getChildrenWithProps()}
			</li>
		);
	}
}

